<script>
import thunderAudio from "../static/music/eclipse.mp3";
import anime from "animejs/lib/anime.es.js";
/* require('../assets/js/MorphSVGPlugin.min.js'); */
export default {
  data: () => ({
    listSVG: [],
    lineDrawing: null
  }),
  beforeMount() {
    this.listSVG = this.$store.state.svg.all;
  },
  mounted() {
    this.preparaLoader();
    /* this.audioContext = document.getElementById('audio-elengy'); */

    /* document.getElementById('btn-pause-audio').addEventListener('click', ()=>  {
        this.audioContext.pause();
    }); */
  },
  methods: {
    preparaLoader() {
      setTimeout(() => {
        const logo = document.getElementById("logo-init");
        logo.style.display = "flex";
      }, 400);
      let svgElengy = document.getElementById("svg-elengy");
      anime({
        targets: ".morph-svg",
        strokeDashoffset: [anime.setDashoffset, 0],
        easing: "easeInOutCubic",
        duration: 3000,
        delay: 1600,
        begin: function(anim) {
          document.querySelector(".morph-svg").setAttribute("stroke", "white");
          document.querySelector(".morph-svg").setAttribute("fill", "none");
        },
        complete: anim => {
          document.querySelector(".morph-svg").setAttribute("fill", "#aa0000");
          let bgDark = new TimelineMax();
          bgDark
            .to(".loader", .8, {
              delay: 0,
              background: "#fff"
            })
            .to(".loader", 1, {
              background: "#1c2131",
              delay: 2.8,
              onStart: () => {
                let morphing = anime({
                  targets: ".morph-svg",
                  d: [
                    { value: this.listSVG[2].path },
                    { value: this.listSVG[3].path }
                  ],
                  easing: "easeInOutQuint",
                  duration: 800,
                  loop: false
                });
                let textWrapper = document.querySelector(".ml7 .letters");
                textWrapper.innerHTML = textWrapper.textContent.replace(
                  /([^\x00-\x80]|\w)/g,
                  "<span class='letter'>$&</span>"
                );
                setTimeout(() => {
                  document.querySelectorAll(".ml7")[0].style.visibility =
                    "visible";
                }, 100);
                anime.timeline({ loop: false }).add({
                  targets: ".ml7 .letter",
                  translateY: ["1.1em", 0],
                  translateX: ["0.55em", 0],
                  translateZ: 0,
                  rotateZ: [180, 0],
                  duration: 750,
                  easing: "easeOutExpo",
                  delay: function(el, i) {
                    return 50 * i;
                  }
                });
              },
              onComplete: () => {
                document
                  .querySelector(".morph-svg")
                  .setAttribute("fill", "orange");
                  /* document.querySelectorAll('#svg-elengy path')[0].classList.add('animate-thunder'); */
                  /* thunder.reverse() */

                  document.getElementById('svg-elengy').classList.add('animate-thunder')
              }
            });
        },
        autoplay: true
      });
     
      TweenMax.from("#btn-play-audio", {
        duration: 1.5,
        delay: 6.5,
        opacity: 0,
        scale: 0,
        border: "none",
        ease: Back,
        onComplete: () => {
          setTimeout(() => {
            document.querySelector(".animate-press-button").style.opacity = "1";
          }, 1500);
        }
      });
    },
    difuminaLoader() {
      let tlWipe = new TimelineMax();
      tlWipe
        .to("#svg-elengy, .letter, #btn-play-audio, .animate-press-button", 1, {
          autoAlpha: 0,
          display: "none"
        })
        .to(".loader", 1, {
          scaleY: 0,
          transformOrigin: "center",
          onComplete: function() {
            document.body.style.overflowY = "visible";
            document.documentElement.style.overflow = "visible";
            TweenMax.to(".init", 1, {
              delay: 0,
              /* top: "-200vh", */
              zIndex: -100,
              ease: Expo.easeInOut
            });
          }
        });
    },
    animaIndexPage() {
      TweenMax.from("#logo-elengy-top, #menu", 1, {
        delay: 1,
        opacity: 0,
        y: -50,
        ease: Back
      });
      TweenMax.from(".link-nav, .hamburger", 1, {
        delay: 1,
        opacity: 0,
        stagger: 0.5,
        y: 0,
        ease: Back
      });
      TweenMax.from("#banner-top", 1, {
        delay: 4,
        opacity: 0,
        y: -100,
        ease: Back.easeInOut
      });
      TweenMax.from("#header-elengy", 1, {
        delay: 1,
        opacity: 0,
        y: 400,
        ease: Expo.easeInOut
      });
    },
    closeLoader() {
      this.$emit("loadClosing");
